import React, { Component } from "react";

import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      min: 0,
      sec: 0,
      milli: 0
    };
  }

  fastTick = () => {
    this.setState({ milli: this.state.milli + 1 });
    if (this.state.milli === 100) {
      this.setState({ milli: 0 });
    }
  };

  componentDidMount() {
    setInterval(() => {
      this.fastTick();
    }, 1 / 100);
  }

  render() {
    return (
      <div className="App">
        <h1>StopWatch</h1>
        <div className="screen">{this.state.milli}</div>
        <div>
          <button className="btns btn-1">Start</button>
          <button className="btns btn-2">Reset</button>
        </div>
      </div>
    );
  }
}

export default App;
