import React, { Component } from "react";

import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      min: 0,
      sec: 0,
      milli: 0,
      text: "Start",
      counter: 0
    };
    var fastTickCounter;
    var secTickCounter;
  }

  fastTick = () => {
    this.setState({ milli: this.state.milli + 1 });
    if (this.state.milli === 100) {
      this.setState({ milli: 0 });
    }
  };
  secTick = () => {
    this.setState({ sec: this.state.sec + 1 });
    if (this.state.sec === 60) {
      this.setState({ sec: 0 }, { min: this.state.min + 1 });
    }
  };

  startTimer = () => {
    var newText = this.state.text === "Start" ? "Reset" : "Start";
    this.setState({ text: newText });
    this.fastTickCounter = setInterval(() => {
      this.fastTick();
    }, 1 / 100);
    this.secTickCounter = setInterval(() => {
      this.secTick();
    }, 1000);
  };

  stopTimer = () => {
    clearInterval(this.fastTickCounter);
    clearInterval(this.secTickCounter);
    return (
      <div className="screen">
        {this.state.min} : {this.state.sec} : {this.state.milli}
      </div>
    );
  };

  render() {
    return (
      <div className="App">
        <h1>StopWatch</h1>
        <div className="screen">
          {this.state.min} : {this.state.sec} : {this.state.milli}
        </div>
        <div>
          <button className="btns btn-1" onClick={this.startTimer}>
            {this.state.text}
          </button>
          <button className="btns btn-2" onClick={this.stopTimer}>
            Stop
          </button>
        </div>
      </div>
    );
  }
}

export default App;

const formattedSeconds = sec =>
  Math.floor(sec / 60) + ":" + ("0" + (sec % 60)).slice(-2);

class Stopwatch extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      secondsElapsed: 0,
      laps: [],
      lastClearedIncrementer: null
    };
    this.incrementer = null;
  }

  handleStartClick() {
    this.incrementer = setInterval(
      () =>
        this.setState({
          secondsElapsed: this.state.secondsElapsed + 1
        }),
      1000
    );
  }

  handleStopClick() {
    clearInterval(this.incrementer);
    this.setState({
      lastClearedIncrementer: this.incrementer
    });
  }

  handleResetClick() {
    clearInterval(this.incrementer);
    this.setState({
      secondsElapsed: 0,
      laps: []
    });
  }

  handleLabClick() {
    this.setState({
      laps: this.state.laps.concat([this.state.secondsElapsed])
    });
  }

  render() {
    return (
      <div className="stopwatch">
        <h1 className="stopwatch-timer">
          {formattedSeconds(this.state.secondsElapsed)}
        </h1>

        {this.state.secondsElapsed === 0 ||
        this.incrementer === this.state.lastClearedIncrementer ? (
          <Button
            className="start-btn"
            onClick={this.handleStartClick.bind(this)}
          >
            start
          </Button>
        ) : (
          <Button
            className="stop-btn"
            onClick={this.handleStopClick.bind(this)}
          >
            stop
          </Button>
        )}

        {this.state.secondsElapsed !== 0 &&
        this.incrementer !== this.state.lastClearedIncrementer ? (
          <Button onClick={this.handleLabClick.bind(this)}>lab</Button>
        ) : null}

        {this.state.secondsElapsed !== 0 &&
        this.incrementer === this.state.lastClearedIncrementer ? (
          <Button onClick={this.handleResetClick.bind(this)}>reset</Button>
        ) : null}

        <ul className="stopwatch-laps">
          {this.state.laps.map((lap, i) => (
            <li className="stopwatch-lap">
              <strong>{i + 1}</strong>/ {formattedSeconds(lap)}
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

/** verbose component before 0.14
class Button extends React.Component {
  render() {
    return <button type="button" {...this.props}
                   className={"btn " + this.props.className } />;
  }
}
*/

const Button = props => (
  <button type="button" {...props} className={"btn " + props.className} />
);

React.render(<Stopwatch />, document.body);
