import React, { Component } from "react";

import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      min: 0,
      sec: 0,
      milli: 0,
      text: "Start"
    };
  }

  fastTick = () => {
    this.setState({ milli: this.state.milli + 1 });
    if (this.state.milli === 100) {
      this.setState({ milli: 0 });
    }
  };
  secTick = () => {
    this.setState({ sec: this.state.sec + 1 });
    if (this.state.sec === 60) {
      this.setState({ sec: 0 }, { min: this.state.min + 1 });
    }
  };

  startTimer = () => {
    var newText = this.state.text === "Start" ? "Stop" : "Start";
    this.setState({ text: newText });
    setInterval(() => {
      this.fastTick();
    }, 1 / 100);
    setInterval(() => {
      this.secTick();
    }, 1000);
  };

  render() {
    return (
      <div className="App">
        <h1>StopWatch</h1>
        <div className="screen">
          {this.state.min} : {this.state.sec} : {this.state.milli}
        </div>
        <div>
          <button className="btns btn-1" onClick={this.startTimer}>
            {this.state.text}
          </button>
          <button className="btns btn-2">Reset</button>
        </div>
      </div>
    );
  }
}

export default App;
