import React, { Component } from "react";

import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      min: null,
      sec: null,
      milli: null
    };
  }

  fastTick = () => {
    this.setState({ milli: milli + 1 });
  };

  componentDidMount() {
    setInterval(() => {
      this.fastTick();
    }, 1 / 1000);
  }

  render() {
    return (
      <div className="App">
        <h1>StopWatch</h1>
        <div className="screen" />
        <div>
          <button className="btns btn-1">Start</button>
          <button className="btns btn-2">Reset</button>
        </div>
      </div>
    );
  }
}

export default App;
